import React from 'react'

class EditRecipe extends React.Component {
    constructor(){
        super()
        this.state = {
            newName: "",
            description: "",
            recipes: []
        }
    }
    render(){
        return (
            <div className = "editrecipe">
            <h1>Edit a Recipe</h1>
            <div className = "editdelete">
            <form>
                <div className = "box1">
                <label>Recipe:</label>
                <br/>
                <select 
                    ref = "input"
                    value = {this.state.recipe}
                    onChange = {this.onChange}>
                    {
                        this.state.recipes.map((recipe) => {
                            return (
                                <option
                                    key = {recipe}
                                    value = {recipe}>
                                {recipe}
                                </option>
                            )
                        })
                    }
                </select>
                </div>
                <br/>
                <div className = "box1">
                <label>New Name:</label>
                <br/>
                <input type = "text"/>
                </div>
                <br/>
                <div className = "box1">
                <label>New Description:</label>
                <br/>
                <textarea />
                <button>Delete Recipe</button>
                </div>
                <br/>
                <div className = "submit"><input type = "submit" value = "Edit Recipe"/></div>
            </form>
            </div>
            </div>
        )
    }
}


export default EditRecipe
